import React from "react";
import {
  View,
  Text,
  Button,
  Image,
  TouchableOpacity,
  StyleSheet,
} from "react-native";

function CarSelection({ route }) {
  const { pickup, destination } = route.params;
  const fares = {
    bike: 50,
    rickshaw: 80,
    car: 100,
    bus: 150,
    truck: 200,
    airplane: 1000,
  };
  const calculateFare = (vehicle) => {
    const { latitude: pickupLat, longitude: pickupLong } = pickup.geocodes.main;
    const { latitude: destinationLat, longitude: destinationLong } =
      destination.geocodes.main;
    console.log(pickup, destination);
    const distance = calcCrow(
      pickupLat,
      pickupLong,
      destinationLat,
      destinationLong
    );

    const fare = fares[vehicle] * distance;
    alert("Rs" + fare.toFixed(2));
  };
  //This function takes in latitude and longitude of two location and returns the distance between them as the crow flies (in km)
  function calcCrow(lat1, lon1, lat2, lon2) {
    var R = 6371; // km
    var dLat = toRad(lat2 - lat1);
    var dLon = toRad(lon2 - lon1);
    var lat1 = toRad(lat1);
    var lat2 = toRad(lat2);

    var a =
      Math.sin(dLat / 2) * Math.sin(dLat / 2) +
      Math.sin(dLon / 2) * Math.sin(dLon / 2) * Math.cos(lat1) * Math.cos(lat2);
    var c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    var d = R * c;
    return d;
  }

  // Converts numeric degrees to radians
  function toRad(Value) {
    return (Value * Math.PI) / 180;
  }
  return (
    <View>
      <Text style={styles.text}> Select Your Ride</Text>

      <View>
        <Text style={styles.para}>Your Selected pickup Location is</Text>
        <Text>
          {pickup.name},{pickup.location.address}
        </Text>
      </View>

      <View>
        <Text style={styles.para}>Your Selected destination Location is</Text>
        <Text>
          
          {destination.name},{destination.location.address}
        </Text>
      </View>

      <View style={styles.container}>
        <TouchableOpacity onPress={() => calculateFare("bike")}>
          <Image
            source={{
              uri: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSMCc1QyN3XzZu9Uykxyn7Pmu5lF0SoB5wcgg&usqp=CAU",
            }}
            style={{
              width: 100,
              height: 100,
              borderRadius: 50,
              resizeMode: "fit",
            }}
          />
        </TouchableOpacity>
        <TouchableOpacity onPress={() => calculateFare("rickshaw")}>
          <Image
            source={{
              uri: "data:image/jpeg;base64,",
            }}
            style={{
              width: 100,
              height: 100,
              borderRadius: 50,
              resizeMode: "fit",
            }}
          />
        </TouchableOpacity>
        <TouchableOpacity onPress={() => calculateFare("car")}>
          <Image
            source={{
              uri: "data:image/jpeg;base64,",
            }}
            style={{
              width: 100,
              height: 100,
              borderRadius: 50,
              resizeMode: "fit",
            }}
          />
        </TouchableOpacity>
        <TouchableOpacity onPress={() => calculateFare("bus")}>
          <Image
            source={{
              uri: "data:image/jpeg;base64,",
            }}
            style={{
              width: 100,
              height: 100,
              borderRadius: 50,
              resizeMode: "fit",
            }}
          />
        </TouchableOpacity>
      </View>
      {/* <View>
                <Button onPress={() => calculateFare('bike')}  />
                <Button onPress={() => calculateFare('rickshaw')} title={`rickshaw |  ${fares.rickshaw} Rs./ km`} />
                <Button onPress={() => calculateFare('car')} title={`car |  ${fares.car} Rs./ km`} />
                <Button onPress={() => calculateFare('bus')} title={`bus |  ${fares.bus} Rs./ km`} />
            </View> */}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flexDirection: "row", // Change this value to see different layouts
    justifyContent: "center",
    alignItems: "center",
    padding: 50,
    justifyContent: "space-around",
  },
  text: {
    fontSize: 20,
    fontWeight: "700",
    paddingBottom:30,
    margin:10,
  },
  para: {
    fontSize: 18,
    
   
  },
});

